import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { AccountContext } from "./context";
import { useParams } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const { userBal, user, setUser } = useContext(AccountContext);
  const params = useParams();
  const { username } = params;
  // const [profData, setProfData] = useState([]);

  useEffect(() => {
    const profileData = async () => {
      const apiUrl = `http://localhost:5000/api/users/${username}`;
      axios
        .get(apiUrl)
        .then((response) => {
          // Handle successful response
          // setProfData(response.data);
          // console.log(profData);
          setUser(response.data.username);
          console.log(user);
          console.log("Response data: ", response.data);
        })
        .catch((error) => {
          // Handle error
          console.error("Error:", error.message);
        });
    };
    profileData();
  });

  return (
    <div className="d-flex justify-content-center mt-4">
      <div className="card mx-auto my-auto col-md-8 col-lg-6 col-sm-10 border border-3 border-dark shadow-lg">
        <div className="row d-flex my-auto">
          <div className="card-body d-flex my-auto">
            <div className="container">
              <h1 className="card-title d-flex justify-content-center m-4">
                welcome back {username}! and {name}
              </h1>
              <div className="fs-3">
                here's your sorry ass balance: &nbsp;${userBal}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
