import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { AccountContext } from "./context";
import { useParams } from "react-router-dom";
import axios from "axios";

const Profile = () => {

  const [profileData, setProfileData] = useState('');
  const { userBal } = useContext(AccountContext);
  const params = useParams();
  const { username } = params;


  useEffect(() => {
    const fetchProfile = async (username) => {
      try {
        const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage
        const response = await axios.get(`http://localhost:5000/api/profile/${username}`, {}, {
          headers: {
            Authorization: token // Include the JWT token in the request headers
          }
        });
      setProfileData(response.data.user.username); // Assuming the response contains userProfile

    } catch (error) {

      console.error('Error fetching profileData:', error);
    }
  };
  fetchProfile();
}, [username, profileData]); 

  /////     /////     /////     /////     /////     /////     /////     /////     /////     

  return (
    <div className="d-flex justify-content-center mt-4">
      <div className="card mx-auto my-auto col-md-8 col-lg-6 col-sm-10 border border-3 border-dark shadow-lg">
        <div className="row d-flex my-auto">
          <div className="card-body d-flex my-auto">
            <div className="container">
              <h1 className="card-title d-flex justify-content-center m-4">
                Welcome back {profileData.username}!
              </h1>
              <div className="fs-3">
                Here's your sorry ass balance: &nbsp;${userBal}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
