import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { AccountContext } from "./context";
import { useParams } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const { userBal } = useContext(AccountContext);
  const params = useParams();
  const { username } = params;

  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage
        const response = await axios.get(`http://localhost:5000/api/profile/${username}`, {
          headers: {
            Authorization: token // Include the JWT token in the request headers
          }
        });
        setProfileData(response.data); // Set profile data received from the API
      } catch (error) {
        console.error('Error fetching profile data:', error);
        setError('An error occurred while fetching profile data.');
      } finally {
        setLoading(false); // Set loading state to false regardless of success or failure
      }
    };
    fetchProfile();
  }, [username]);

  if (loading) {
    return <div>Loading...</div>; // Display a loading indicator while fetching data
  }

  if (error) {
    return <div>Error: {error}</div>; // Display an error message if fetching data fails
  }

  return (
    <div className="d-flex justify-content-center mt-4">
      <div className="card mx-auto my-auto col-md-8 col-lg-6 col-sm-10 border border-3 border-dark shadow-lg">
        <div className="row d-flex my-auto">
          <div className="card-body d-flex my-auto">
            <div className="container">
              <h1 className="card-title d-flex justify-content-center m-4">
                Welcome back {username}!
              </h1>
              <div className="fs-3">
                Here's your balance: ${userBal}
              </div>
              {profileData && (
  <div className="fs-3">
    <div>Full Name: {profileData.fullName}</div>
    <div>Bio: {profileData.bio}</div>
    <div>Avatar: {profileData.avatar}</div>
  </div>
)}
              {/* Display profile data */}
              {profileData && (
                <div className="fs-3">Profile Username: {profileData.username}</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
