import React, { useState, createContext, useEffect } from "react";

export const AccountContext = createContext();

const AccountContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [allUsers, setAllUsers] = useState([]);
  const [userBal, setUserBal] = useState(0);
  const [pastBal, setPastBal] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [profileData, setProfileData] = useState(null)
  const [balance, setBalance] = useState(0); // Initialize balance to 0


  useEffect(() => {
    const fetchBalance = async () => {
      try {
        const response = await fetch(`/api/balance/${currentUser}/getBalance`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            // Include any necessary authorization headers
          },
        });
        const data = await response.json();
        setBalance(data.balance);
      } catch (error) {
        console.error('Error fetching balance:', error);
      }
    };

    fetchBalance();
    console.log("currentUser context useEffect: " + currentUser);
  }, [currentUser]);

  const registerUser = (registerUserData) => {
    setUser(registerUserData)
    setAllUsers((prevAllUsers) => {
      return [...prevAllUsers, user];
    });
  }

  const login = (loginUserData) => {
    setUser(loginUserData);

    setAllUsers((prevAllUsers) => {
      return [...prevAllUsers, user];
    });
    console.log("current func in context " + currentUser)
  };

  const giveth = (n) => {
    setUserBal(userBal + n);
    setPastBal((prevPastBal) => {
      return [...prevPastBal, userBal];
    });
  };

  const taketh = (n) => {
    setUserBal(userBal - n);
    setPastBal((prevPastBal) => {
      return [...prevPastBal, userBal];
    });
  };

  const contextValue = {
    balance,
    profileData,
    setProfileData,
    registerUser,
    currentUser,
    setCurrentUser,
    userBal,
    giveth,
    pastBal,
    taketh,
    user,
    setAllUsers,
    allUsers,
    login,
  };

  return (
    <AccountContext.Provider value={contextValue}>
      {children}
    </AccountContext.Provider>
  );
};
export default AccountContextProvider;
