import React, { useEffect, useState, useContext } from "react";
import { AccountContext } from "./context";
import axios from "axios";
import { useParams } from "react-router-dom";

const Profile = () => {
  const { userBal, setCurrentUser, currentUser } = useContext(AccountContext);
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const params = useParams();


  useEffect(() => {
    if (!currentUser) {
      setCurrentUser(params.username);
    }
  }, [params.username, currentUser, setCurrentUser]);

  useEffect(() => {
    if (currentUser) {
      const fetchProfile = async () => {
        try {
          const token = localStorage.getItem('token');
          const response = await axios.get(`http://localhost:5000/api/profile/${currentUser}`, {
            headers: { Authorization: token }
          });
          setProfileData(JSON.stringify(response.data));
          setCurrentUser(JSON.stringify(response.data.user.username))
          setLoading(false); // Set loading to false after data is fetched
        } catch (error) {
          console.error('Error fetching profile data:', error);
          setError('An error occurred while fetching profile data. Please try again later.');
          setLoading(false); // Set loading to false in case of error
        }
      };
      fetchProfile();
    }
  }, [currentUser, params.username, setCurrentUser]);


  if (loading) {
    return <div>Loading...</div>; // Render loading state
  }

  if (error) {
    return <div>Error: {error}</div>; // Render error state
  }

  return (
    <div className="d-flex justify-content-center mt-4">
      <div className="card mx-auto my-auto col-md-8 col-lg-6 col-sm-10 border border-3 border-dark shadow-lg">
        <div className="row d-flex my-auto">
          <div className="card-body d-flex my-auto">
            <div className="container">
              <h1 className="card-title d-flex justify-content-center m-4">
                Welcome back {params.username}! {profileData && profileData.email}
              </h1>
              <div className="fs-3">
                Here's your balance: ${userBal}
              </div>
              {profileData && (
                <div className="fs-3">
                  <div>Bio: {profileData.bio}</div>
                  <div>Avatar: {profileData.avatar}</div>
                </div>
              )}

            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
