import React, { useState, createContext, useEffect } from "react";

export const AccountContext = createContext();

const AccountContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [allUsers, setAllUsers] = useState([]);
  const [userBal, setUserBal] = useState(0);
  const [pastBal, setPastBal] = useState([]);

  // useEffect(() => {
  //   console.log(user);
  // }, [user]); // Include user in the dependency array if needed

  const login = (userData) => {
    setUser(userData);
    setAllUsers((prevAllUsers) => [...prevAllUsers, userData]);
  };

  const giveth = (n) => {
    setUserBal((prevUserBal) => prevUserBal + n);
    setPastBal((prevPastBal) => [...prevPastBal, userBal]);
  };

  const taketh = (n) => {
    setUserBal((prevUserBal) => prevUserBal - n);
    setPastBal((prevPastBal) => [...prevPastBal, userBal]);
  };

  const contextValue = {
    userBal,
    giveth,
    pastBal,
    taketh,
    setUser,
    user,
    setAllUsers,
    allUsers,
    login,
  };

  return (
    <AccountContext.Provider value={contextValue}>
      {children}
    </AccountContext.Provider>
  );
};

export default AccountContextProvider;
