import React, { useState, createContext, useEffect } from "react";

export const AccountContext = createContext();

const AccountContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [allUsers, setAllUsers] = useState([]);
  const [userBal, setUserBal] = useState(0);
  const [pastBal, setPastBal] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);



  useEffect((user) => {
    console.log("user context useEffect: " + user);
    console.log("currentUser context useEffect: " + currentUser);
  }, [user, currentUser]);

  const registerUser = (registerUserData) => {

    setAllUsers((prevAllUsers) => {
      return [...prevAllUsers, user];
    });
  }

  const login = (userData) => {
    setAllUsers((prevAllUsers) => {
      return [...prevAllUsers, user];
    });
    setUser(userData);
    setCurrentUser({ userData })
    console.log("current func in context " + currentUser)
  };

  const giveth = (n) => {
    setUserBal(userBal + n);
    setPastBal((prevPastBal) => {
      return [...prevPastBal, userBal];
    });
  };

  const taketh = (n) => {
    setUserBal(userBal - n);
    setPastBal((prevPastBal) => {
      return [...prevPastBal, userBal];
    });
  };

  const contextValue = {
    registerUser,
    currentUser,
    setCurrentUser,
    userBal,
    giveth,
    pastBal,
    taketh,
    user,
    setAllUsers,
    allUsers,
    login,
  };

  return (
    <AccountContext.Provider value={contextValue}>
      {children}
    </AccountContext.Provider>
  );
};
export default AccountContextProvider;
