import React, { useState } from "react";
import axios from "axios";

const Post = () => {
  const [decision, setDecision] = useState(false);
  const [content, setContent] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    setDecision(true);

    try {
      const response = axios.post(
        "http://localhost:5000/api/post",
        content, // Send an object with the content property
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log("Response:", response.data); // Log the response data if needed
    } catch (error) {
      console.error("Error during Post:", error);
    }
  };

  const handleInputChange = (e) => {
    setContent(e.target.value); // Update content state with input value
  };

  return (
    <>
      <form onSubmit={(e) => handleSubmit(e)}>
        <input type="text" value={content} onChange={handleInputChange} />
        <button type="submit">Submit</button>
      </form>
      {decision && <div>{content}</div>}
    </>
  );
};

export default Post;
