import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { AccountContext } from "./context";
import { useParams } from "react-router-dom";
import axios from "axios";
const Profile = () => {
  const { userBal } = useContext(AccountContext);
  const [theUser, setTheUser] = useState(null);
  const params = useParams();
  const { username } = params;

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const apiUrl = `http://localhost:5000/api/users/${username}`;
        const response = await axios.get(apiUrl);
        setTheUser(response.data.user); // Assuming the user data is nested under 'user'
      } catch (error) {
        console.error("Error fetching profile data:", error.message);
      }
    };

    fetchProfileData();
  }, [username, setTheUser]); // Include setTheUser in the dependency array

  return (
    <div className="d-flex justify-content-center mt-4">
      <div className="card mx-auto my-auto col-md-8 col-lg-6 col-sm-10 border border-3 border-dark shadow-lg">
        <div className="row d-flex my-auto">
          <div className="card-body d-flex my-auto">
            <div className="container">
              <h1 className="card-title d-flex justify-content-center m-4">
                Welcome back {theUser.username}!
              </h1>
              <div className="fs-3">
                Here's your sorry ass balance: &nbsp;${userBal}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
