// Profile.js

import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { AccountContext } from "./context";
// import { useParams } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const { userBal} = useContext(AccountContext);
  // const params = useParams();
  // const { username } = params;
  const [ currentUser, setCurrentUser] = useState();

  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
const storedUserData = JSON.parse(localStorage.getItem('currentUser'));
  setCurrentUser(JSON.stringify(storedUserData.username))
console.log(currentUser)

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5000/api/auth/${currentUser}`, {
          headers: { Authorization: token }
        });
        setProfileData(response.data.user.username);
      } catch (error) {
        console.error('Error fetching profile data:', error);
        setError('An error occurred while fetching profile data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, [currentUser]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="d-flex justify-content-center mt-4">
      <div className="card mx-auto my-auto col-md-8 col-lg-6 col-sm-10 border border-3 border-dark shadow-lg">
        <div className="row d-flex my-auto">
          <div className="card-body d-flex my-auto">
            <div className="container">
              <h1 className="card-title d-flex justify-content-center m-4">
                Welcome back {currentUser}!
              </h1>
              <div className="fs-3">
                Here's your balance: ${userBal}
              </div>
              {profileData && (
                <div className="fs-3">
                  <div>Full Name: {profileData.fullName}</div>
                  <div>Bio: {profileData.bio}</div>
                  <div>Avatar: {profileData.avatar}</div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
