import React, { useEffect, useState, useContext } from "react";
import { AccountContext } from "./context";
import axios from "axios";
import { useParams } from "react-router-dom";

const Profile = () => {
  const { userBal } = useContext(AccountContext);
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentUser, setCurrentUser] = useState('');
  const params = useParams();

  const storedUserData = JSON.parse(localStorage.getItem('currentUser'));

  setCurrentUser(storedUserData.username);
  console.log("c" + currentUser)
  console.log(storedUserData)


  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        if (currentUser) {
          const response = await axios.get(`http://localhost:5000/api/auth/${params.username}`, {
            headers: { Authorization: token }
          });
          setProfileData(response.data);
          setLoading(false); // Set loading to false after data is fetched
        } else {
          setError('User not found');
          setLoading(false); // Set loading to false in case of error
        }
      } catch (error) {
        console.error('Error fetching profile data:', error);
        setError('An error occurred while fetching profile data. Please try again later.');
        setLoading(false); // Set loading to false in case of error
      }
    };

    if (currentUser) {
      fetchProfile();
    }
  }, [currentUser, params.username]);

  if (loading) {
    return <div>Loading...</div>; // Render loading state
    // console.log(response)
  }

  if (error) {
    return <div>Error: {error}</div>; // Render error state
  }

  return (
    <div className="d-flex justify-content-center mt-4">
      <div className="card mx-auto my-auto col-md-8 col-lg-6 col-sm-10 border border-3 border-dark shadow-lg">
        <div className="row d-flex my-auto">
          <div className="card-body d-flex my-auto">
            <div className="container">
              <h1 className="card-title d-flex justify-content-center m-4">
                Welcome back {params.username}! {profileData && profileData.email}
              </h1>
              <div className="fs-3">

                Here's your balance: ${userBal}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
